const dados =  JSON.parse($('AI Agent1').first().json.output);
const periodo = dados.periodo;
const valor = dados.valor;

//const now = new Date();
const now = new Date(
  new Date().toLocaleString('en-US', { timeZone: 'America/Sao_Paulo' })
);

// ===== Helpers =====
const pad = (n) => String(n).padStart(2, "0");
const formatDate = (d) => {
  if (d instanceof Date && !isNaN(d)) {
    return `${pad(d.getDate())}/${pad(d.getMonth() + 1)}/${d.getFullYear()}`;
  }
  return null; // Retorna null se a data for inv√°lida
};
const formatTime = (d) =>
  `${pad(d.getHours())}:${pad(d.getMinutes())}:${pad(d.getSeconds())}`;
const cloneDate = (d) => new Date(d.getTime());

// ===== Resultado =====
let result = {
  dispositivo: null,
  hora_inicio: null,
  hora_fim: null,
  data_inicio: null,
  data_fim: null,
};

// ===== Switch determin√≠stico =====
switch (periodo) {
  case "hoje": {
    const d = formatDate(now);
    result.data_inicio = d;
    result.data_fim = d;
    break;
  }
  case "ontem": {
    const d = cloneDate(now);
    d.setDate(d.getDate() - 1);
    const fd = formatDate(d);
    result.data_inicio = fd;
    result.data_fim = fd;
    break;
  }
  case "ultimas_24_horas": {
    const start = cloneDate(now);
    start.setHours(start.getHours() - 24);
    result.data_inicio = formatDate(start);
    result.data_fim = formatDate(now);
    result.hora_inicio = formatTime(start);
    result.hora_fim = formatTime(now);
    break;
  }
  case "ultima_semana": {
    const start = cloneDate(now);
    start.setDate(start.getDate() - 7);
    result.data_inicio = formatDate(start);
    result.data_fim = formatDate(now);
    break;
  }
  case "ultimo_mes": {
    const start = cloneDate(now);
    start.setDate(start.getDate() - 30);
    result.data_inicio = formatDate(start);
    result.data_fim = formatDate(now);
    break;
  }
  case "ultimos_X_dias": {
    if (typeof valor === "number" && valor > 0) {
      const start = cloneDate(now);
      start.setDate(start.getDate() - valor);
      result.data_inicio = formatDate(start);
      result.data_fim = formatDate(now);
    }
    break;
  }
  case "intervalo_datas": {
    // As datas j√° v√™m formatadas como DD/MM/YYYY do classificador
    if (dados.data_inicio) {
      result.data_inicio = dados.data_inicio;
    }
    if (dados.data_fim) {
      result.data_fim = dados.data_fim;
    }
    break;
  }
  case "nenhum":
  default: {
    // Se o usu√°rio n√£o informou m√™s/ano, assume o m√™s/ano corrente
    const start = new Date(now.getFullYear(), now.getMonth(), 1);
    const end = new Date(now.getFullYear(), now.getMonth() + 1, 0);
    result.data_inicio = formatDate(start);
    result.data_fim = formatDate(end);
    break;
  }
}

return result;

// ==============================
// Helpers
// ==============================
function parseDateBR(dateStr) {
  if (!dateStr) return null;

  const [day, month, year] = dateStr.split('/').map(Number);
  return new Date(year, month - 1, day, 0, 0, 0);
}

// ==============================
// Datas do filtro (vindas do agente)
// ==============================
const filtro = $items('Code in JavaScript2')[0]?.json || {};

const dataInicio = parseDateBR(filtro.data_inicio);
const dataFim = parseDateBR(filtro.data_fim);

// Prote√ß√£o forte
if (!dataInicio || !dataFim) {
  return [];
}

// ==============================
// Filtro + normaliza√ß√£o
// ==============================
const resultado = [];

for (const item of items) {
  const linha = item.json;
  const dataLinha = parseDateBR(linha.DATA);

  if (!dataLinha) continue;

  const t = dataLinha.getTime();

  if (
    t >= dataInicio.getTime() &&
    t <= dataFim.getTime()
  ) {
    // üîπ Aqui voc√™ entrega o JSON LIMPO
    resultado.push(linha);
  }
}

// ==============================
// Sa√≠da FINAL para o HTML
// ==============================
return [
  {
    json: {
      relatorio: resultado,
      total: resultado.length,
      periodo: {
        inicio: filtro.data_inicio,
        fim: filtro.data_fim
      }
    }
  }
];

// Fun√ß√£o correta para acessar bin√°rio no n8n
